package com.example.schooladmin.view.adapter;import android.content.Context;import android.content.Intent;import android.view.LayoutInflater;import android.view.MenuItem;import android.view.View;import android.view.ViewGroup;import android.widget.Adapter;import android.widget.PopupMenu;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.schooladmin.R;import com.example.schooladmin.model.Student;import com.example.schooladmin.view.adapter.viewholder.StudentViewHolder;import java.util.List;public class StudentRecyclerAdapter extends RecyclerView.Adapter<StudentViewHolder> {    private List<Student> students;    private Context context;    public StudentRecyclerAdapter(List<Student> students, Context context) {        this.students = students;        this.context = context;    }    @NonNull    @Override    public StudentViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from( context ).inflate( R.layout.item_recycler_student, parent, false );        return new StudentViewHolder( view );    }    @Override    public void onBindViewHolder(@NonNull StudentViewHolder holder, int position) {        holder.getTxtRecyclerStudentName().setText( students.get( holder.getAdapterPosition() ).getName() );        holder.getTxtRecyclerStudentClassName().setText( students.get( holder.getAdapterPosition() ).getClassName() );        holder.getTxtRecyclerStudentPhone().setText( students.get( holder.getAdapterPosition() ).getPhone() );        holder.getView().setOnLongClickListener( new View.OnLongClickListener() {            @Override            public boolean onLongClick(View view) {                PopupMenu popupMenu = new PopupMenu( context, holder.getView() );                popupMenu.inflate( R.menu.option_menu );                popupMenu.setOnMenuItemClickListener( new PopupMenu.OnMenuItemClickListener() {                    @Override                    public boolean onMenuItemClick(MenuItem menuItem) {                        switch (menuItem.getItemId()) {                            case R.id.deleteItem:                                break;                            case R.id.updateItem:                                break;                        }                        return false;                    }                } );                popupMenu.show();                return true;            }        } );    }    @Override    public int getItemCount() {        return students.size();    }    public void setDate(List<Student> students) {        this.students = students;        notifyDataSetChanged();    }}