package com.example.schooladmin.view.adapter;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;import com.example.schooladmin.R;import com.example.schooladmin.model.ClassSchool;import com.example.schooladmin.model.Subject;import java.util.List;public class SubjectSpinnerAdapter extends BaseAdapter {    private Context context;    private List<Subject> subjects;    public SubjectSpinnerAdapter(Context context, List<Subject> subjects) {        this.context = context;        this.subjects = subjects;    }    @Override    public int getCount() {        return subjects.size();    }    @Override    public Object getItem(int i) {        return subjects.get( i );    }    @Override    public long getItemId(int i) {        return i;    }    @Override    public View getView(int i, View view, ViewGroup viewGroup) {        LayoutInflater layoutInflater = (LayoutInflater) context.getSystemService( Context.LAYOUT_INFLATER_SERVICE );        view = layoutInflater.inflate( R.layout.item_spinner_classes, null );        TextView txtSpinnerClassesName = view.findViewById( R.id.txtSpinnerClassesName );        txtSpinnerClassesName.setText( subjects.get( i ).getName() );        return view;    }    public void setData(List<Subject> subjects) {        this.subjects = subjects;        notifyDataSetChanged();    }}