package com.example.schooladmin.view.adapter.viewholder;import android.view.View;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.schooladmin.R;import org.w3c.dom.Text;public class StudentViewHolder extends RecyclerView.ViewHolder {    private View view;    private TextView txtRecyclerStudentName;    private TextView txtRecyclerStudentClassName;    private TextView txtRecyclerStudentPhone;    public StudentViewHolder(@NonNull View view) {        super( view );        this.view = view;        txtRecyclerStudentName = view.findViewById( R.id.txtRecyclerStudentName );        txtRecyclerStudentPhone = view.findViewById( R.id.txtRecyclerStudentPhone );        txtRecyclerStudentClassName = view.findViewById( R.id.txtRecyclerStudentClassName );    }    public View getView() {        return view;    }    public TextView getTxtRecyclerStudentName() {        return txtRecyclerStudentName;    }    public TextView getTxtRecyclerStudentClassName() {        return txtRecyclerStudentClassName;    }    public TextView getTxtRecyclerStudentPhone() {        return txtRecyclerStudentPhone;    }}