package com.example.schooladmin.view.adapter.viewholder;import android.view.View;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.schooladmin.R;public class TeacherViewHolder extends RecyclerView.ViewHolder {    private View view;    private TextView txtRecyclerTeacherName;    private TextView txtRecyclerTeacherSubjectName;    public TeacherViewHolder(@NonNull View view) {        super( view );        this.view = view;        txtRecyclerTeacherName = view.findViewById( R.id.txtRecyclerTeacherName );        txtRecyclerTeacherSubjectName = view.findViewById( R.id.txtRecyclerTeacherSubjectName );    }    public View getView() {        return view;    }    public TextView getTxtRecyclerTeacherName() {        return txtRecyclerTeacherName;    }    public TextView getTxtRecyclerTeacherSubjectName() {        return txtRecyclerTeacherSubjectName;    }}