package com.example.schooladmin.view.fargment;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.content.IntentFilter;import android.content.SharedPreferences;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.fragment.app.Fragment;import com.example.schooladmin.R;import com.example.schooladmin.query.ProfileQuery;import com.example.schooladmin.view.activity.HomeActivity;import static android.content.Context.MODE_PRIVATE;public class ProfileFragment extends Fragment {    private TextView txtNameAccountFragmentProfile;    private TextView txtPasswordAccountFragmentProfile;    private SharedPreferences sharedPreferences;    private ProfileQuery profileQuery;    public View onCreateView(@NonNull LayoutInflater inflater,                             ViewGroup container,                             Bundle savedInstanceState) {        View view = inflater.inflate( R.layout.fragment_profile, container, false );        sharedPreferences = ((HomeActivity) getActivity()).getSharedPreferences( "adminID", MODE_PRIVATE );        int adminID = sharedPreferences.getInt( "adminID", 0 );        profileQuery = new ProfileQuery( getActivity() );        profileQuery.callTaskGetInfoProfileByID( adminID );        txtNameAccountFragmentProfile = view.findViewById( R.id.txtNameAccountFragmentProfile );        txtPasswordAccountFragmentProfile = view.findViewById( R.id.txtPasswordAccountFragmentProfile );        IntentFilter intentFilter = new IntentFilter( "adminInfo" );        requireActivity().registerReceiver( broadcastReceiverAdminInfo, intentFilter );        return view;    }    BroadcastReceiver broadcastReceiverAdminInfo = new BroadcastReceiver() {        @Override        public void onReceive(Context context, Intent intent) {            txtNameAccountFragmentProfile.setText( profileQuery.getAdminInfo().getName() );            txtPasswordAccountFragmentProfile.setText( profileQuery.getAdminInfo().getPassword() );        }    };}